# How to get a string 

# Can either use single quotes or triple quotes
str1 = "This is a string"
str2 = """this is also a string"""
# One difference between single and triple quotes is that, in the latter case, you can use quotation marks within your string.

println("""triple strings are "lit"!""")

# '' are for chars not strings 
println(typeof('j'))
# this is an error: 'jake'

# Can use $ for string interpolation

name = "Jake"
room_num = 607
println("Hello, my name is $name")
println("I live in room number $room_num")

# we can either use the string() method or * to concatenate strings
# string() method makes a non-string a string

str3 = "Hey, whats up? Hello?"
str4 = "fetty"
println(string(str3, str4))
println(string("One hit wonder in", 2015))

print(str3*str4)
hi = repeat("hi", 1000)
@assert hi == ""

a = 3
b = 4
c = string(a, " + ", b)
d = string(a+b)
println(c)
@assert c == "3 + 4"
println(d)
@assert d == "7"
